def find_chongfu(L1):
    L2 = L1
    for i in range(len(L2)):
        tmp = L2[i]
        L2.pop(i)
        if(tmp in L2):
            print("%d:%d"%(i, tmp))
        L2.insert(i, tmp)

def find_nixushu(L1):
    L2 = L1
    for i in range(len(L2)-1):
        if(L2[i+1] < L2[i]):
            print("%dth:model%d"%(i+1, L2[i+1]))
    
   
        




